import { Box, Typography, Grid, Paper,MenuItem,TextField } from '@mui/material'
import HelpOutlinedIcon from '@mui/icons-material/HelpOutlined';
import { useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import React, { useEffect, useState } from "react";
import { useParams } from "react-router";
import { Actiontypes } from '../Reducer/action'
import { Connect } from 'react-redux';


const account = [
  {
    value:'Stock',
    label: 'Stock', 
  },
  {
    value:'Demat',
    label: 'Demat', 
  },
  {
    value:'Savings',
    label:'Savings'
  }

];

export const Edit = ({updatedData}) => {
  const { id } = useParams();
 const navigate = useNavigate();
 const dispatch = useDispatch() ;
 const contacts = useSelector((state) => state);

 const contact = contacts.find(
    (contact) => contact.id === parseInt(id)
  );



  const [name, setName] = useState("");
  const [type, setType] = useState("");
  const [code,setCode] = useState("");
  const [description, setDescription] = useState("");


  useEffect(() => {
    setName(contact.name);
    setType(contact.type);
    setCode(contact.code)
    setDescription(contact.description);
  }, [contact]);

  const handleSubmit = (e) => {
    e.preventDefault();

     const data = {
      id: contacts.length > 0 ? contacts[contacts.length - 1].id + 1 : 0, 
      name,
      type,
      code,
      description
    };
    updatedData(data)
    dispatch({type: Actiontypes.UPDATE_CONTACT,payload:data});

    navigate("/user-list");
  };




  return (
    <Box>
    <Grid container justifyContent={'center'}>
    <Grid item xs={12} sm={8} md={6} sx={{ marginTop: 2, boxShadow: 'rgba(0, 0, 0, 0.35) 0px 5px 15px' }}>
      <Paper
        sx={{ padding: '17px', height: 'fit-content', marginLeft: '7px' }}
      >
    <div >
      <div >

        <div >
          {contact ? (
             <form>
             <Typography sx={{ fontSize: '20px' }}>Edit Account</Typography>
             <Box>
               <label>
                 <Typography mt={5} mb={1} sx={{ fontSize: "16px", color: "#BD4C46" }}>
                   Account Name
                 </Typography>
               </label>

               <input
                 type='text'
                 value={name}
                 onChange={(e) => setName(e.target.value)}

                 style={{ height: '30px', width: '250px' }}
               />
             </Box>

             <Box>
               <label>
                 <Typography mt={3} mb={1} sx={{ fontSize: "16px", }}>
                   Account Code <HelpOutlinedIcon sx={{ height: '18px', position: 'absolute', marginTop: '2px' }} />
                 </Typography>
               </label>

               <input
                 type='text'
                 value={code}
                 onChange={(e) => setCode(e.target.value)}
                 style={{ height: '30px', width: '250px' }}
               />
             </Box>

             <Box>
               <label>
                 <Typography mt={3} mb={1} sx={{ fontSize: "16px", color: "#BD4C46" }}>
                   Account Type
                 </Typography>
               </label>
               <TextField

         select
         defaultValue="EUR"    
         value={type}
         onChange={(e) => setType(e.target.value)}
         sx={{width:250}}
       >
         {account.map((option) => (
           <MenuItem key={option.value} value={option.value}>
             {option.label}
           </MenuItem>
         ))}
       </TextField>


             </Box>

             <Box>
               <label>
                 <Typography
                   mt={3}
                   mb={1}
                   sx={{ fontSize: "16px", color: "#000" }}
                 >
                   Description
                 </Typography>
               </label>

               <textarea
                 rows={3}
                 cols={25}
                 placeholder="Max 500 characters"
                 value={description}
                 onChange={(e) => setDescription( e.target.value)}
                 style={{
                   padding: "10px",
                   borderRadius: "2px",
                   border: "1px solid gray",
                   fontFamily: 'sans-serif',
                   fontSize: '15px'
                 }}
               />
             </Box>

             <Box>
               <Typography mt={3}><input type="checkbox"  ></input>
                 <label style={{ fontFamily: 'inherit', marginLeft: '5px' }}>Add to the watchlist on my dashboard</label></Typography>
             </Box>

             <Box mt={6} >
               <button style={{ height: '40px', width: '65px', backgroundColor: '#CF4B28', color: 'white', border: '1px solid #CF4B28', borderRadius: '5px', fontWeight: 'bold' }} 
                onClick={handleSubmit} >Save</button>
               <button style={{ height: '40px', width: '65px', marginLeft: '11px', backgroundColor: 'lightgrey', border: '1px solid lightgray', borderRadius: '5px' }}>Cancel</button>
               <button
         style={{ height: '40px', width: '75px',float:'right', backgroundColor: '#CF4B28', color: 'white', border: '1px solid #CF4B28', borderRadius: '5px', fontWeight: 'bold' }} 
          onClick={() => navigate("/user-list")}
        >
          Go back
        </button>
             </Box>



           </form>
          ) : (
            <h1 className="text-center">No Contact Found</h1>
          )}
        </div>
      </div>
    </div>
    </Paper>
        </Grid>
      </Grid>
    </Box>
  );

 }
 const mapStateToProps = state => ({
  data: state.data,
});

// Map the dispatch functions to props
const mapDispatchToProps = dispatch => ({
  fetchData: () => dispatch({ type: 'FETCH_DATA' }),
  addData: (data) => dispatch({ type: 'ADD_DATA', payload: data }),
  updateData: (id, contact) => dispatch({ type: 'UPDATE_CONTACT', payload: { id, ...contact } }),
  deleteData: (id) => dispatch({ type: 'DELETE_DATA', payload: id }),
});

// Connect the component to the Redux store
export default connect(mapStateToProps, mapDispatchToProps)(Edit);
