const initialState=[
    {
        id:1,
        name:'Surya Prakash',
        code:'#12384',
        type:'Stock',
        description:'Easy to handle'
    },
    {
        id:2,
        name:'Surya ',
        code:'#12384',
        type:'Demat',
        description:'Hello'
    },
]

const contactReducer = (state = initialState, action) => {
    console.log(contactReducer)
    switch(action.type){
        case "ADD_ITEMS":
            state = [...state,action.payload];
            return state;
            case "UPDATE_CONTACT":
                const updateContact = state.filter((contact) =>
                  contact.id === action.payload ?  contact : null
                );
                state = updateContact;
                return state; 




            case "DELETE_CONTACT":
                const contactFilter = state.filter((contact) =>
                  contact.id === action.payload ? null : contact
                );
                state = contactFilter;
                return state;

        default : return state;

    }


}

export default contactReducer;
