import { Button } from '@mui/material'
import React,{useEffect} from 'react'
import { json, Link } from 'react-router-dom'
import { useSelector } from 'react-redux'
import '../Details/UserList.css'





export const UserList = () => {
  const contacts = useSelector((state) => state)

const updateUser = ({name,type,description}) => {
  // const data = {
  //   id: contacts.length > 0 ? contacts[contacts.length - 1].id + 1 : 0, 
  //   name,
  //   type, 
  //   description
  // };

  localStorage.setItem('name',name)
  localStorage.setItem('type',type)
  localStorage.setItem('description',description)

}

useEffect((e) => {
  e.preventDefault;
}, [])



  return (
    <div>
      <Link to="/"> <button  style={{backgroundColor:'#CF4B28',color:'white',border:'1px solid #CF4B28',borderRadius:'6px',padding:'5px'}}>Add user</button> </Link>
      <div className='table-items'>
        <table >
          <thead>
            <tr style={{backgroundColor:'#CF4B28',color:'white'}}>
              <th>id</th>
              <th>Name</th>
              <th>Type</th>
              <th>Description</th>
              <th>Edit</th>
              <th>Delete</th>
            </tr>
          </thead>
          <tbody >


            {contacts.map((contact, id) => (
              <tr key={id} >
                <td>{id + 1}</td>
                <td>{contact.name}</td>
                <td>{contact.type}</td>
                <td>{contact.description}</td>
                <td>
                 <Link to={'/edit'}> <Button style={{

                    backgroundColor:'#CF4B28',
                    color:'white',
                    border:'1px solid #CF4B28',borderRadius:'6px',padding:'5px'}}
                   onClick={ updateUser}
                    >
                         update
                  </Button></Link>
                 </td>

                 <td>
                  <button style={{
                    backgroundColor:'#CF4B28',
                    color:'white',
                    border:'1px solid #CF4B28',borderRadius:'6px',padding:'5px'
                    }}>
                          Delete
                  </button>
                 </td>



              </tr>))}
          </tbody>

        </table>
      </div>
      {/* <Link to={`edit-user/${users.id}`}> <Button>edit user</Button> </Link> */}
    </div>
  )
}
