import { Button } from '@mui/material'
import React,{useEffect} from 'react'
import { Link } from 'react-router-dom'
import { useSelector } from 'react-redux'
import '../Details/UserList.css'





 export const UserList = ({deleteAccount}) => {

   const contacts = useSelector((state) => state);
   const dispatch = useDispatch();





  return (
    <div>
      <Link to="/"> <button  style={{backgroundColor:'#CF4B28',color:'white',border:'1px solid #CF4B28',borderRadius:'6px',padding:'5px'}}>Add user</button> </Link>
      <div className='table-items'>
        <table >
          <thead>
            <tr style={{backgroundColor:'#CF4B28',color:'white'}}>
              <th>id</th>
              <th>Name</th>
              <th>Type</th>
              <th>Description</th>
              <th>Edit</th>
              <th>Delete</th>
            </tr>
          </thead>
          <tbody >


            {contacts.map((contact, id) => (
              <tr key={id} >
                <td>{id + 1}</td>
                <td>{contact.name}</td>
                <td>{contact.type}</td>
                <td>{contact.description}</td>
                <td>
                 <Link to={'/edit'}> <button style={{
                    backgroundColor:'#CF4B28',
                    color:'white',
                    border:'1px solid #CF4B28',borderRadius:'6px',padding:'5px',

                    }}  onClick={() => {
                      dispatch({type:"ADD_ITEMS",payload:contacts});
            }}>
                         update
                  </button></Link>
                 </td>

                 <td>
                  <button style={{
                    backgroundColor:'#CF4B28',
                    color:'white',
                    border:'1px solid #CF4B28',borderRadius:'6px',padding:'5px'
                    }}
                    onClick={() => deleteAccount(contact.id)}>
                          Delete
                  </button>
                 </td>
              </tr>))}
          </tbody>

        </table>
      </div>
      {/* <Link to={`edit-user/${users.id}`}> <Button>edit user</Button> </Link> */}
    </div>
  )
}
const mapStateToProps = (state) => ({
  contacts: state,
});

const mapDispatchToProps = (dispatch) => ({
  deleteContact: (id) => {
    dispatch({ type: "DELETE_CONTACT", payload: id });
  },
});
export default (mapStateToProps, mapDispatchToProps)(UserList);
