import React from 'react'
import {Box, Typography,Grid,TextField, Paper} from '@mui/material'
import { color } from '@mui/system'

export const Details = () => {
  return (
    <Box >
        <Grid container justifyContent={'center'}>
  <Grid item xs={4} md={8}  sx={{marginTop:4,boxShadow: 'rgba(0, 0, 0, 0.35) 0px 5px 15px'}}> 
  <Paper
   sx={{padding:'10px',height:'fit-content'}}
  >
  <Typography >Create Account</Typography>

  <FormGroup>
              <label>
                <Typography
                  mt={2}
                  mb={1}
                  sx={{ fontSize: "17px", color: "#000" }}
                >
                  <b> Description </b>
                </Typography>
              </label>

              <textarea
                rows={4}
                cols={50}
                placeholder="Max 500 Characters"
                style={{
                  padding: "10px",
                  borderRadius: "5px",
                  width: "300px",
                  border: "1px solid #000",
                }}
              />
            </FormGroup>


  <label>Description</label><br/>
   <textarea rows={4} cols={10} placeholder="Max 500 characters" style={{color:'gray'}} > </textarea>


  </Paper> 
  </Grid>
</Grid>

        </Box>
  )
}
