const initialState =[
    { id: 0, name: "Raman Sharma", code: 1234567890,type: "Stock",description:'yes it is good '  },
    { id: 1, name: "Sharma", code: 1234567890,type: "Stock",description:'yes it is good '  },
]

const contactReducer = (state = initialState , action) => {
    switch(action.type){
        case "ADD_ITEMS":
            state = {...state,action}
        default : return state;
    }
}

export default contactReducer;
