const initialState=[
    {
        id:0,
        name:'Surya Prakash',
        code:'#12384',
        type:'Stock',
        description:'Easy to handle'
    },
]

const contactReducer = (state = initialState, action) => {
    switch(action.type){
        case "ADD_ITEMS":
            state = [...state,action.payload];
            return state;

            case 'UPDATE_CONTACT':

        const index = state.findIndex(r => r.id === action.payload.id);

        return [
          ...state.slice(0, index),
          action.payload,
          ...state.slice(index + 1)
        ];

        return state;


            case "DELETE_CONTACT":
                const contactFilter = state.filter((contact) =>
                  contact.id === action.payload ? null : contact
                );
                state = contactFilter;
                return state;

        default : return state;

    }


}

export default contactReducer;
