} 



  return (
    <div>
      <Link to="/"> <button  style={{backgroundColor:'#CF4B28',color:'white',border:'1px solid #CF4B28',borderRadius:'6px',padding:'5px'}}>Add user</button> </Link>
      <div className='table-items'>
        <table >
          <thead>
            <tr style={{backgroundColor:'#CF4B28',color:'white'}}>
              <th>id</th>
              <th>Name</th>
              <th>Type</th>
              <th>Description</th>
              <th>Edit</th>
              <th>Delete</th>
            </tr>
          </thead>
          <tbody >


          {contacts.length > 0 ? (
                contacts.map((contact, id) => (
                  <tr key={id}>
                    <td>{id + 1}</td>
                    <td>{contact.name}</td>
                    <td>{contact.description}</td>
                    <td>{contact.code}</td>
                    <td>
                      <Link
                        to={`/edit/${contact.id}`}
                        className="btn btn-sm btn-primary mr-1"
                      >
                        Edit
                      </Link>
                      <button
                        type="button"
                        onClick={() => deleteAccount(contact.id)}
                        className="btn btn-sm btn-danger"
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <th>No contacts found</th>
                </tr>
              )}
          </tbody>

        </table>
      </div>
      {/* <Link to={`edit-user/${users.id}`}> <Button>edit user</Button> </Link> */}
    </div>
  )
}
const mapStateToProps = (state) => ({
  contacts: state,
});

const mapDispatchToProps = (dispatch) => ({
  deleteAccount: (id) => {
    dispatch({ type: "DELETE_CONTACT", payload: id });
  },
});
export default (mapStateToProps, mapDispatchToProps)(UserList);
